func DeleteByStatus(db *gorm.DB, minFlag int, deleteBefore time.Time) ([]Performance, error) {
// 固定参数
const (
fixedStatus = "inactive"
maxRecords = 100
)
// 参数验证
if db == nil {
return nil, fmt.Errorf("database connection cannot be nil")
}
if minFlag < 0 {
return nil, fmt.Errorf("minFlag must be non-negative")
}
if deleteBefore.IsZero() {
return nil, fmt.Errorf("deleteBefore must be a valid time")
}
var performances []Performance
err := db.Table("game_statistics").
Select("id, name, flag, status, created_at").
Where("flag > ?", minFlag).
Where("status = ?", fixedStatus).
Where("created_at < ?", deleteBefore).
Order("created_at asc").
Limit(maxRecords).
Find(&performances).Error
if err != nil {
return nil, err
}
return performances, nil
}

