func MassUpdate(minRating int, productIDs []int) ([]Wishlist, error) {
// Fixed parameters
minFee := 50.0
maxFee := 200.0
isVerified := true
startDate := time.Date(2023, 1, 1, 0, 0, 0, 0, time.UTC)
endDate := time.Date(2023, 12, 31, 23, 59, 59, 0, time.UTC)
// Parameter validation
if minRating < 0 {
return nil, fmt.Errorf("minimum rating cannot be negative")
}
if len(productIDs) == 0 {
return nil, fmt.Errorf("product IDs list cannot be empty")
}
// Determine which ORM method to call based on parameters
var results []Wishlist
var err error
if minRating > 0 && len(productIDs) > 0 {
// Full parameters case
results, err = GetByStatus(minFee, maxFee, minRating, isVerified, startDate, endDate, productIDs)
} else if minRating > 0 {
// Mixed case with minRating but no productIDs
results, err = GetByStatus(minFee, maxFee, minRating, isVerified, startDate, endDate, nil)
} else {
// Fixed parameters only case
results, err = GetByStatus(minFee, maxFee, 0, isVerified, startDate, endDate, productIDs)
}
if err != nil {
return nil, fmt.Errorf("failed to update wishlist: %v", err)
}
return results, nil
}

