func SumByDate(db *gorm.DB, email string, period int, width float64, height float64) ([]Profile, error) {
if email == "" {
return nil, fmt.Errorf("email cannot be empty")
}
var profiles []Profile
// Fixed parameters: return_reason IN ('defective', 'wrong_item') AND return_status = 'pending'
// Dynamic parameters: period, width, height
query := db.Table("route_optimization")
.Where("email = ?", email)
.Where("return_reason IN (?, ?)", "defective", "wrong_item")
.Where("return_status = ?", "pending")
if period > 0 {
query = query.Where("period > ?", period)
}
if width > 0 {
query = query.Where("width BETWEEN ? AND ?", 5.0, 20.0)
}
if height > 0 {
query = query.Where("height < ?", height)
}
result := query.Order("request_date DESC").Limit(100).Find(&profiles)
if result.Error != nil {
return nil, result.Error
}
if len(profiles) == 0 {
return nil, gorm.ErrRecordNotFound
}
return profiles, nil
}

